data:
$bem-element: '__' !default;
$bem-modifier: '--' !default;
$-bem-block-name: '' !default;
$-bem-element-name: '';

@mixin -bem-check-wrapper {
  @if $-bem-block-name == '' {
    @error 'You must wrap this mixin inside block mixin wrapper';
  }
}

@mixin block ($block-name) {
  $-bem-block-name: '.#{$block-name}' !global;

  .#{$block-name} {
    @content;
  }
  $-bem-block-name: '' !global;
}

@mixin element ($element-name) {
  $element-name: #{$bem-element}#{$element-name};
  $-bem-element-name: $element-name !global;

  @include -bem-check-wrapper;

  @if str-index(inspect(&), $bem-element) {
    & #{$-bem-block-name}#{$element-name} {
      @content;
    }
  } @else if str-index(inspect(&), ':hover') {
    & #{$-bem-block-name}#{$element-name} {
      @content;
    }
  } @else if str-index(inspect(&), $bem-modifier) {
    & #{$-bem-block-name}#{$element-name} {
      @content;
    }
  } @else {
    @at-root &#{$element-name} {
      @content;
    }
  }
  $-bem-element-name: '' !global;
}

@mixin modifier ($modifier-name) {
  $modifier-name: #{$bem-modifier}#{$modifier-name};
  @include -bem-check-wrapper;

  @if str-index(inspect(&), ':hover') {
    $length: length(str-explode(inspect(&), ' '));

    @if $length >= 2 {
      @at-root &#{$modifier-name} {
        $parent: &;
        @content;
      }
    } @else {
      $element-name: nth(str-replace(nth(str-explode(inspect(&), ':'), 1), '(', ''), 1);

      &#{$element-name}#{$modifier-name} {
        @content;
      }
    }
  } @else {
    &#{$modifier-name} {
      @content;
    }
  }
}

/*
  @a-pollo-doc
  @author: Vittorio Vittori
  @category: BEM
  @code: Example on using the mixin with some static property
    @include block (button) {
      background: #3f6c44;
      color: #fff;
      display: inline-block;
      font-size: 12px;
      padding: 4px 8px;
    }
  @css: This is the CSS generated
    .button {
      background: #3f6c44;
      color: #fff;
      display: inline-block;
      font-size: 12px;
      padding: 4px 8px;
    }
  @date: 2016-12-28T17:40:42+01:00
  @type: mixin
  @html: A simple HTML element to see how mixin works
    <a class="button" href="#">Visit this link</a>
  @icon: fa fa-css3
  @name: block
  @param: {string} ($block-name) [required]
          Defines the block name of the BEM component
  @public: true
  @returns: css
  @text: Defines the block name of the BEM component. This mixin is required as wrapper of `element` and `modifier` mixins
  @version: 4.0.0
*/

/*
  @a-pollo-doc
  @author: Vittorio Vittori
  @category: BEM
  @code: You must wrap `element` mixin inside `block` mixin
    @include block (button) {
      background: #3f6c44;
      display: inline-block;
      padding: 4px 8px;

      @include element (text) {
        color: #fff;
        font-size: 12px;
      }
    }
  @css: The CSS generated will keep everything on 1 class level deep
    .button {
      background: #3f6c44;
      display: inline-block;
      padding: 4px 8px;
    }
    .button__text {
      color: #fff;
      font-size: 12px;
    }
  @date: 2016-12-28T18:09:00+01:00
  @type: mixin
  @html: Elements should be inside a parent block
    <a class="button" href="#">
      <span class="button__text">Visit this link</span>
    </a>
  @icon: fa fa-css3
  @name: element
  @param: {string} ($element-name) [required]
          Defines the element name of the BEM component
  @public: true
  @returns: css
  @text: Defines the element name of the BEM component. This mixin is required as wrapped inside `block` mixin
  @version: 4.0.0
*/

/*
  @a-pollo-doc
  @author: Vittorio Vittori
  @category: BEM
  @code: Modifiers can be applied to `block` or `element` mixins
    @include block (button) {
      background: #3f6c44;
      color: #fff;
      display: inline-block;
      font-size: 12px;
      padding: 4px 8px;

      @include modifier (error) {
        background: #f02052;
      }
    }
  @css:
    .button {
      background: #3f6c44;
      color: #fff;
      display: inline-block;
      font-size: 12px;
      padding: 4px 8px;
    }
    .button--error {
      background: #f02052;
    }
  @date: 2016-12-28T18:09:00+01:00
  @type: mixin
  @html: Remember to keep the modifier's original block or element selector
    <a class="button button--error" href="#">Click here to report the error</a>
  @icon: fa fa-css3
  @name: modifier
  @param: {string} ($modifier-name) [required]
          Defines the modifier name of the BEM component
  @public: true
  @returns: css
  @text: Defines the modifier name of the BEM component. This mixin is required as wrapped inside `block` mixin
  @version: 4.0.0
*/

/*
  @a-pollo-doc
  @author: Vittorio Vittori
  @category: Functions
  @code: Usage example
    .selector {
      width: scale-value(40px, 0.5);
    }
  @css: The CSS generated
    .selector {
      width: 20px;
    }
  @date: 2017-01-05T15:01:54+01:00
  @type: function
  @icon: fa fa-terminal
  @name: scale-value
  @param: {unit-value} ($value) [required]
          The unit value to be scaled, es: `10px`, `2em`, etc.
  @param: {number} ($scale) [required]
          The scale number to change the value, es: `0.5`, `1`, `1.75`, etc.
  @public: true
  @returns: unit-value
  @text: Scales unit values passed, the return value will be rounded.
  @version: 4.0.0
*/

@function scale-value($value, $scale) {
  @return round($value * $scale);
}

@function spacing($scale: 1) {
  @return scale-value($spacing, $scale);
}

@function s($scale: 1, $value: null) {
  @if $value == null {
    @return spacing($scale);
  } @else {
    @return scale-value($value, $scale);
  }
}

@function step($step, $total-steps: $row-columns-total) {
  @return ($step * 100 / $total-steps) * 1%;
}

@function get($map, $attribute) {
  @if map-has-key($map, $attribute) {
    @return map-get($map, $attribute);
  } @else {
    @error 'No attribute \'#{$attribute}\' found inside map #{$map}\n'
    + 'Please checkout your code to fine where is the problem.';
  }
}

@mixin -button-social($text, $background) {
  background-color: $background;
  color: mix($text, $background, 70%);

  &:hover {
    color: $text;
  }

  @include element ('icon') {
    color: $text;
  }
}

/*
  @a-pollo-snippet
  @name: button-social
  @auth: [Vittorio Vittori](http://vit.to)
  @date: 2017-01-06T17:50:18+01:00
  @category: Buttons
  @text: The base usage without styles of the button, this component **requires** [FontAwesome](http://fontawesome.github.io) to be loaded as dependency.
  @html:
    <a href="#" class="button-social">
      <div class="button-social__icon">
        <i class="fa fa-dashboard"></i>
      </div>
      <div class="button-social__network">
        Button
      </div>
    </a>
*/

.button-social {

  border-radius: 4px;
  cursor: pointer;
  display: inline-block;
  padding: s(0.25) s(0.5);
  text-decoration: none;
  transition: color 0.125s ease-out;
  user-select: none;
  width: auto;

  @include -button-social(#fff, #777);

  @include element ('icon') {
    display: table-cell;
    text-align: center;
    white-space: nowrap;
    width: 18px;
  }

  @include element ('network') {
    display: table-cell;
    font-size: 14px;
    padding-left:  s(0.25);
    text-transform: capitalize;
  }

  /*
    @a-pollo-snippet
    @name: button-social--small
    @auth: [Vittorio Vittori](http://vit.to)
    @date: 2015-12-03
    @text: The GitHub version with `button-social--small` and `button-social--with-effects` modifiers.
    @html:
      <a href="#" class="button-social button-social--small">
        <div class="button-social__icon">
          <i class="fa fa-compress"></i>
        </div>
        <div class="button-social__network">
          Small button
        </div>
      </a>
  */
  @include modifier ('small') {
    padding: s(0.125) s(0.25);

    @include element ('network') {
      font-size: 12px;
      padding-left:  s(0.125);
    }
  }

  /*
    @a-pollo-snippet
    @name: button-social--rounded
    @auth: [Vittorio Vittori](http://vit.to)
    @date: 2017-01-06T17:54:08+01:00
    @text: This Facebook version has also `button-social--rounded` modifier to set it with rounded corners.
    @html:
      <a href="#" class="button-social button-social--rounded">
        <div class="button-social__icon">
          <i class="fa fa-life-ring"></i>
        </div>
        <div class="button-social__network">
          Rounded button
        </div>
      </a>
  */
  @include modifier ('rounded') {
    border-radius: 100px;
    padding-left: s(0.75);
    padding-right: s(0.75);
  }

  /*
    @a-pollo-snippet
    @name: button-social--big
    @auth: [Vittorio Vittori](http://vit.to)
    @date: 2016-01-09
    @text: This Facebook version has also `button-social--big` modifier to make it bigger.
    @html:
      <a href="#" class="button-social button-social--big">
        <div class="button-social__icon">
          <i class="fa fa-expand"></i>
        </div>
        <div class="button-social__network">
          Big button
        </div>
      </a>
  */
  @include modifier ('big') {
    padding: s(0.5) s(0.75);
  }

  /*
    @a-pollo-snippet
    @auth: [Vittorio Vittori](http://vit.to)
    @name: button-social--with-effects
    @date: 2017-01-06T17:56:58+01:00
    @html:
      <a href="#" class="button-social button-social--with-effects">
        <div class="button-social__icon">
          <i class="fa fa-magic"></i>
        </div>
        <div class="button-social__network">
          Button with effects
        </div>
      </a>
  */
  @include modifier ('with-effects') {
    $shadow-area: rgba(0, 0, 0, 0.2);
    $shadow-line: rgba(255, 255, 255, 0.3);
    box-shadow: 0 1px 2px $shadow-area, inset 0 1px 0 $shadow-line;
  }

  /*
    @a-pollo-snippet
    @auth: [Vittorio Vittori](http://vit.to)
    @date: 2017-01-06T17:57:20+01:00
    @name: button-social--facebook
    @html:
      <a href="#" class="button-social button-social--facebook">
        <div class="button-social__icon">
          <i class="fa fa-facebook"></i>
        </div>
        <div class="button-social__network">
          facebook
        </div>
      </a>
    @text: This should be used to connect social accounts to the project service.
  */
  @include modifier ('facebook') {
    @include -button-social(white, #3b5998);
  }

  /*
    @a-pollo-snippet
    @auth: [Vittorio Vittori](http://vit.to)
    @name: button-social--twitter
    @date: 2017-01-06T18:01:30+01:00
    @html:
      <a href="#" class="button-social button-social--twitter button-social--with-effects">
        <div class="button-social__icon">
          <i class="fa fa-twitter"></i>
        </div>
        <div class="button-social__network">
          twitter
        </div>
      </a>
  */
  @include modifier ('twitter') {
    @include -button-social(white, #55acee);
  }

  /*
    @a-pollo-snippet
    @name: button-social--github
    @auth: [Vittorio Vittori](http://vit.to)
    @date: 2017-01-06T17:59:33+01:00
    @html:
      <a href="#" class="button-social button-social--github">
        <div class="button-social__icon">
          <i class="fa fa-github-alt"></i>
        </div>
        <div class="button-social__network">
          github
        </div>
      </a>
  */
  @include modifier ('github') {
    @include -button-social(#333, #f5f5f5);
  }

  /*
    @a-pollo-snippet
    @auth: [Vittorio Vittori](http://vit.to)
    @date: 2017-01-06T18:02:15+01:00
    @name: button-social--google-plus
    @html:
      <a href="#" class="button-social button-social--google-plus">
        <div class="button-social__icon">
          <i class="fa fa-google-plus"></i>
        </div>
        <div class="button-social__network">
          googe plus
        </div>
      </a>
  */
  @include modifier ('google-plus') {
    @include -button-social(white, #dc4e41);
  }
}
